import { useState, useEffect } from 'react'
import React, { Component }from 'react' 
import './App.css';

const celula = ({pokeClass, handleOnClick}) => 
{
  return <button onClick={() => 
  handleOnClick(pokeClass + 1)} 
  className="poke-cell">
         </button>
}


const Lista =({handleOnClick}) =>{
  const [escolha, poke] = useState([])
    useEffect(() =>{
      const fetchData = async() =>{
      const response = await fetch('https://pokeapi.co/api/v2/ability/?limit=20')
      const data = await response.json()
      const listapoke = data.results.slice(0, data.results.length).map(item => item).flat()
      poke(listapoke)
    }
    fetchData()
    },[])

    return(
      <section className="listapoke">
        {
        escolha.map((pokeClass, id)=>{
          return(
            <celula key={id} 
            pokeClass={id} 
            handleOnClick={handleOnClick} 
            />
          )
        })}
      </section>
    )
}





class App extends Component { 

  constructor() {
    super()
    this.state={}
  }


  render() {
    return(
      <div className="App">
        <h1>Oi</h1>
      </div>
    )
  }
}

export default App;
